import { useState, useEffect, useContext } from "react";
import Card from "react-bootstrap/Card";
import { useParams } from "react-router-dom";
import MySpinner from "../layout/MySpinner";
import "./Detail.css";
import { MyUserContext } from "../App";
import Apis, { authApi, endpoints } from "../configs/Apis";
import SideMenu from "./SideMenu";
import Food from "./Food";

const Detail = () => {
  const [user] = useContext(MyUserContext);
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [comments, setComments] = useState(null);
  const [content, setContent] = useState();

  useEffect(() => {
    const loadProduct = async () => {
        let {data} = await Apis.get(endpoints['details'](productId));
        setProduct(data); 
    }

    const loadComments = async () => {
        let {data} = await Apis.get(endpoints['comments'](productId));
        setComments(data);
    }

    loadProduct();
    loadComments();
}, []);

const addComment = () => {
    const process = async () => {
        let {data} = await authApi().post(endpoints['add-comment'], {
            "content": content, 
            "product": product.id
        });

        setComments([...comments, data]);
    }

    process();
}

if (product === null || comments === null)
    return <MySpinner /> ;

let url = `/login?next=/products/${productId}`;
  return (
    <>
      <Card className="bg-light text-white" id="intro-res">
        <Card.Img className="img" src={item.strMealThumb} alt="Card image" />
        <Card.ImgOverlay className="restaurant-overlay">
          <Card.Title className="text-overlay">{item.strMeal}</Card.Title>
          <Card.Text className="text-overlay">{item.strArea}</Card.Text>
        </Card.ImgOverlay>
      </Card>
      <div className="restaurant-content">
        <SideMenu />
        <Food />
      </div>
    </>
  );
};

export default Detail;
