import { Container, Row, Col, Card, ListGroup, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./Restaurant.css";
import { useEffect, useState } from "react";
import MySpinner from "../layout/MySpinner";
import Apis, { endpoints } from "../configs/Apis";

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState();
  // const[q] = useSearchParams();

  const loadRes = async () => {
    let res = await Apis.get(endpoints["restaurants"]);
    setRestaurants(res.data);
    console.log(res.data);
  };

  useEffect(
    () => {
      loadRes();
    },

    // let kw = q.get{"kw"};
    // if (kw !== null)
    //   e = `${e}?kw=${kw}`;
    []
  );

  if (restaurants === null) return <MySpinner />;

  return (
    <>
      <Container className="dish-container">
        <Row>
          {restaurants && restaurants.map((res) => {
            <>
              <Col xs={12} md={3}>
                <Card
                  style={{ width: "18rem" }}
                  key={res.id}
                  className="dish-card"
                >
                  <Card.Img variant="top" src={res.image} />
                  <Card.Body className="content-res">
                    <Card.Title className="name-res">
                      <Link to={`restaurant/${res.name}`}>{res.name}</Link>
                    </Card.Title>
                    <Card.Text className="address-res">{res.address}</Card.Text>
                  </Card.Body>
                  <ListGroup className="list-group-flush">
                    <ListGroup.Item className="comment">
                      Comment 1
                    </ListGroup.Item>
                    <ListGroup.Item className="comment">
                      Comment 2
                    </ListGroup.Item>
                    <ListGroup.Item className="comment">
                      Comment 3
                    </ListGroup.Item>
                  </ListGroup>
                  <Card.Body>
                    <Button variant="success" className="card-button">
                      Comment
                    </Button>
                    <Button variant="success" className="card-button-save">
                      Save
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            </>;
          })}
        </Row>
      </Container>
    </>
  );
};

export default RestaurantList;
