import { useState, useEffect, useContext } from "react";
import Card from "react-bootstrap/Card";
import { useParams } from "react-router-dom";
import MySpinner from "../layout/MySpinner";
import "./Detail.css";
import { MyUserContext } from "../App";
import Apis, { authApi, endpoints } from "../configs/Apis";
import SideMenu from "./SideMenu";
import Food from "./Food";

const Detail = () => {
  const [user] = useContext(MyUserContext);
  const { restaurantId } = useParams();
  const [restaurant, setRestaurant] = useState();
  const [comments, setComments] = useState(null);
  const [content, setContent] = useState();

  useEffect(() => {
    const loadRestaurant = async () => {
        let {data} = await Apis.get(endpoints['details'](restaurantId));
        setRestaurant(data); 
        console.log(data);
    }

    const loadComments = async () => {
        let {data} = await Apis.get(endpoints['comments'](restaurantId));
        setComments(data);
    }

    loadRestaurant();
    loadComments();
}, []);

const addComment = () => {
    const process = async () => {
        let {data} = await authApi().post(endpoints['add-comment'], {
            "content": content, 
            "restaurant": restaurant.id
        });

        setComments([...comments, data]);
    }

    process();
}

if (restaurant === null || comments === null)
    return <MySpinner /> ;

let url = `/login?next=/products/${restaurantId}`;
  return (
    <>
      <Card className="bg-light text-white" id="intro-res">
        <Card.Img className="img" src={restaurant.image} alt="Card image" />
        <Card.ImgOverlay className="restaurant-overlay">
          <Card.Title className="text-overlay">{restaurant.name}</Card.Title>
          <Card.Text className="text-overlay">{restaurant.address}</Card.Text>
        </Card.ImgOverlay>
      </Card>
      <div className="restaurant-content">
        <SideMenu />
        <Food />
      </div>
    </>
  );
};

export default Detail;
