import {
  Container,
  Nav,
  Navbar,
  NavDropdown,
  Form,
  Button,
  Image,
  Row,
  Col
} from "react-bootstrap";
import { useEffect, useState } from "react";
import logo from "../images/logo.png";
import avatar from "../images/blank_avatar.png";
import { useNavigate } from "react-router-dom";

const Header = () => {
  const [cities, setCities] = useState([]);
  const [cates, setCates] = useState([]);
  const [kw, setKw] = useState("");
  const nav = useNavigate();

  const loadCities = async () => {
    let res = await fetch(
      "https://www.themealdb.com/api/json/v1/1/list.php?a=list"
    );
    let data = await res.json();

    setCities(data.meals);
  };

  const loadCates = async () => {
    let res = await fetch(
      "https://www.themealdb.com/api/json/v1/1/list.php?c=list"
    );
    let data = await res.json();

    setCates(data.meals);
  };
  useEffect(() => {
    loadCities();
    loadCates();
  }, []);

  const search=(evt) =>{
    evt.preventDefault();
    nav(`\?kw=${kw}`)
  }

  const Menu = ['Profile', 'Your orders', 'Settings', 'Log out'];

  return (
    <>
      <Navbar data-bs-theme="light" expand="lg" className="bg-body-tertiary">
        <Container>
          <Navbar.Brand href="#home">
            <img
              alt=""
              src={logo}
              width="50"
              height="50"
              className="d-inline-block align-top"
            />{" "}
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-auto">
              <NavDropdown title="Tỉnh thành" id="basic-nav-dropdown">
                {cities.map((c) => (
                  <NavDropdown.Item className="drop-item" href="#action/3.1">
                    {c.strArea}
                  </NavDropdown.Item>
                ))}
              </NavDropdown>
              <NavDropdown title="Danh mục" id="basic-nav-dropdown" className="nav-dropdown-cate">
                {cates.map((c) => (
                  <NavDropdown.Item className="drop-item" href="#action/3.2">
                    {c.strCategory}
                  </NavDropdown.Item>
                ))}
              </NavDropdown>

              <Form onSubmit={search} inline>
                <Row>
                  <Col xs="auto">
                    <Form.Control
                      type="text"
                      placeholder="Địa điểm , món ăn..."
                      value={kw}
                      onChange={e=>setKw(e.target.value)}
                      className="mr-sm-2"
                      aria-label="Search"
                    />
                  </Col>
                  <Col xs="auto">
                    <Button className="btn-search" variant="outline-success" type="submit">
                      Tìm kiếm
                    </Button>
                  </Col>
                </Row>
              </Form>
            </Nav>
            <div className="relative">
              <Image src={avatar} roundedCircle width="45" height="35" alt="user"/>
              <div className="bg-white p-4 w-52 shadow-lg absolute">
                  <ul>
                    {
                      Menu.map((menu) =>(
                        <li key ={menu}>
                          {menu}
                        </li>
                      ))
                    }
                  </ul>
              </div>
            </div>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
};

export default Header;
