import {Container,Nav, Navbar, NavDropdown, Form, Button, Image} from "react-bootstrap";
import { useEffect, useState } from "react";
import logo from "../images/logo.png";
import avatar from "../images/blank_avatar.png";

const Header = () => {

  const[cities, setCities] = useState([]);
  const[cates, setCates] = useState([]);

  const loadCities = async()=>{
    let res = await fetch("https://www.themealdb.com/api/json/v1/1/list.php?a=list");
    let data = await res.json();

    setCities(data.meals);
  }

  const loadCates = async () => {
    let res = await fetch("https://www.themealdb.com/api/json/v1/1/list.php?c=list");
    let data = await res.json();

    setCates(data.meals);
  }
  useEffect(() => {
    loadCities();

  },[]);

  return (
    <>
      <Navbar
        data-bs-theme="light" 
        expand="lg" 
        className="bg-body-tertiary">
        <Container>
          <Navbar.Brand href= "#home">
          <img
              alt=""
              src={logo}
              width="50"
              height="50"
              className="d-inline-block align-top"
            />{' '}</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-auto">
              <NavDropdown title="Tỉnh thành" id="basic-nav-dropdown">
              {
                cities.map(c=>
                <NavDropdown.Item className="drop-item" href="#action/3.1">{c.strArea}</NavDropdown.Item>)
              }
              </NavDropdown>
              <NavDropdown title="Danh mục" id="basic-nav-dropdown">
                <NavDropdown.Item className="drop-item" href="#action/3.2">Nha hang</NavDropdown.Item>
              </NavDropdown>

              <Form className="d-flex">
                <Form.Control
                  type="search"
                  placeholder="Search"
                  className="mx-auto"
                  aria-label="Search"
                />
                <Button variant="outline-success">Search</Button>
              </Form>
            </Nav>
            <Image src={avatar} roundedCircle width="45" height="35"/>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
};

export default Header;
