import { useState, useEffect, useContext } from "react";
import { Card, Tab, Tabs, Form, Button, ListGroup } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
import MySpinner from "../layout/MySpinner";
import "./Detail.css";
import { MyUserContext } from "../App";
import Apis, { authApi, endpoints } from "../configs/Apis";
import MenuItem from "./MenuItem";
import Moment from "react-moment";

const Detail = () => {
  const [user] = useContext(MyUserContext);
  const { restaurantId } = useParams();
  const [res, setRes] = useState(null);
  const [menu, setMenu] = useState(null);
  const [comments, setComments] = useState(null);
  const [content, setContent] = useState();

  useEffect(() => {
    const loadRestaurant = async () => {
      let { data } = await Apis.get(endpoints["details"](restaurantId));
      setRes(data);
    };

    const loadMenu = async () => {
      let { data } = await Apis.get(endpoints["menu"](restaurantId));
      setMenu(data);
    };

    const loadComments = async () => {
        let {data} = await Apis.get(endpoints['comments'](restaurantId));
        setComments(data);
    }

    loadRestaurant();
    loadMenu();
    loadComments();
  }, []);

  const addComment = () => {
      const process = async () => {
          let {data} = await authApi().post(endpoints['add-comment'], {
              "content": content,
              "restaurant": res.id
          });

          setComments([...comments, data]);
      }

      process();
  }

  if (res === null) return <MySpinner />;

  let url = `/login?next=/restaurant/${restaurantId}`;
  return (
    <>
      <Card className="bg-light text-white" id="intro-res">
        <Card.Img className="img" src={res.image} alt={res.name} />
        <Card.ImgOverlay className="restaurant-overlay">
          <Card.Title className="text-overlay">{res.name}</Card.Title>
          <Card.Text className="text-overlay">{res.address}</Card.Text>
          <button id="follow-button">Follow</button>
        </Card.ImgOverlay>
      </Card>
      <div className="restaurant-content">
        <Tabs
          defaultActiveKey="profile"
          id="justify-tab-example"
          className="mb-3"
          justify
        >
          <Tab eventKey="menu" title="Menu">
            {menu &&
              menu.map((menu) => {
                return (
                  <div className="content-menu">
                  <div>
                    <h4 className="menu-name" key={menu.id}>
                      {menu.name}
                    </h4>
                    <MenuItem id={menu.id} />
                    </div>
                  </div>
                );
              })}
          </Tab>
          <Tab eventKey="comment" title="Bình luận">
          {user===null?<p>Vui lòng <Link to={url}>đăng nhập</Link> để bình luận! </p>:<>
        <Form.Control as="textarea" aria-label="With textarea" value={content} onChange={e => setContent(e.target.value)} placeholder="Nội dung bình luận" />
        <Button onClick={addComment} className="mt-2" variant="info">Bình luận</Button>
        </>}
        <hr />
        <ListGroup>
            {comments && comments.map(c => <ListGroup.Item id={c.id}>
                        {c.user.username} - {c.content} - <Moment locale="vi" fromNow>{c.createdDate}</Moment>
                    </ListGroup.Item>)
            }
        </ListGroup>
          </Tab>
          <Tab eventKey="map" title="Bản đồ">
            Bản đồ
          </Tab>
        </Tabs>
      </div>
    </>
  );
};

export default Detail;
