import {useState, useEffect} from 'react';
import Card from "react-bootstrap/Card";
import { useParams } from 'react-router-dom';
import MySpinner from '../layout/MySpinner';
import './Detail.css';
import SideMenu from './SideMenu';
import Food from './Food';

const Detail = () => {
    const [item, setItem] = useState([]);
    const {id} = useParams();
    const loadItem= async() =>{
        let res = await fetch("https:/www.themealdb.com/api/json/v1/1/lookup.php?i=" + id);
        let data = await res.json();
        
        setItem(data.meals[0]);
      }
    useEffect(() => {
        loadItem();
      }, []);
 
    if (item === null)
      return <MySpinner />;
  return (
    <>
      <Card className="bg-light text-white" id="intro-res">
        <Card.Img className="img" src={item.strMealThumb} alt="Card image" />
        <Card.ImgOverlay className="restaurant-overlay">
          <Card.Title className='text-overlay'>{item.strMeal}</Card.Title>
          <Card.Text className='text-overlay'>{item.strArea}</Card.Text>
        </Card.ImgOverlay>
      </Card>
      <div className='restaurant-content'>
        <SideMenu />
        <FoodList />
      </div>
    </>
  );
};

export default Detail;
