import { useEffect, useState } from "react";
import Apis, { endpoints } from "../configs/Apis";
import "./MenuItem.css";

const MenuItem = (props) => {
  const [item, setItem] = useState([]);
  useEffect(() => {
    const loadItem = async () => {
      let { data } = await Apis.get(endpoints["menu-item"](props.id));
      setItem(data);
      console.log(data);
    };

    loadItem();
  }, []);

  return (
    <>
      {item &&
        item.map((item) => {
          return (
            <>
              <div className="row product">
                <div className="col-md-2">
                  <img src={item.image} alt="Sample Image" height="150" />
                </div>
                <div className="col-md-6 product-detail">
                  <h5>{item.name}</h5>
                  <p>
                    {item.description}
                  </p>
                </div>
                <div className="col-md-2 product-price">{item.price} VNĐ</div>
                <div className="col-md-2" id="order-button">
                    <button>Đặt món</button>
                </div>
              </div>
            </>
          );
        })}
    </>
  );
};

export default MenuItem;
